import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { Modal, Button } from 'react-bootstrap';

const Vision = () => {
  // State for vision data
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [systemActive, setSystemActive] = useState(true);
  const [cameraFeed, setCameraFeed] = useState(null);
  const [detectionStats, setDetectionStats] = useState({
    vehicles: 0,
    pedestrians: 0,
    traffic_signals: 0
  });
  const [incidents, setIncidents] = useState([]);

  // Refs
  const videoRef = useRef(null);

  // Fetch vision data on component mount
  useEffect(() => {
    fetchVisionData();

    // Set up periodic refresh
    const interval = setInterval(fetchVisionData, 10000); // Refresh every 10 seconds

    // Clean up interval on component unmount
    return () => clearInterval(interval);
  }, []);

  // Function to fetch vision data
  const fetchVisionData = async () => {
    try {
      setLoading(true);

      // Fetch vision statistics
      const statsResponse = await api.get('/vision/stats');

      // Update detection stats
      setDetectionStats({
        vehicles: statsResponse.data.detection_counts.vehicle || 0,
        pedestrians: statsResponse.data.detection_counts.pedestrian || 0,
        traffic_signals: (statsResponse.data.detection_counts.traffic_light || 0) + 
                        (statsResponse.data.detection_counts.traffic_sign || 0)
      });

      // Fetch recent detections for incidents
      const detectionsResponse = await api.get('/vision/detections');

      // Filter for emergency incidents
      const emergencyClasses = ['ambulance', 'police_car', 'fire_truck', 'accident', 'fire', 'smoke'];
      const emergencyDetections = detectionsResponse.data.filter(detection => 
        detection.is_emergency
      );

      // Format incidents
      const formattedIncidents = emergencyDetections.map(detection => {
        // Determine severity based on detection type
        let severity = 'warning';
        let title = 'Warning';

        if (['accident', 'fire'].includes(detection.detection_type)) {
          severity = 'critical';
          title = 'Critical Incident';
        }

        return {
          id: detection.detection_id,
          severity: severity,
          title: title,
          description: detection.message || 'Incident detected',
          timestamp: detection.timestamp
        };
      });

      // Add some simulated incidents if none were found
      if (formattedIncidents.length === 0) {
        formattedIncidents.push({
          id: 'sim-1',
          severity: 'critical',
          title: 'Critical Incident',
          description: 'Vehicle sudden brake detected at 50m',
          timestamp: Math.floor(Date.now() / 1000) - 2
        });

        formattedIncidents.push({
          id: 'sim-2',
          severity: 'warning',
          title: 'Warning',
          description: 'Lane deviation detected',
          timestamp: Math.floor(Date.now() / 1000) - 30
        });
      }

      setIncidents(formattedIncidents);

      // Simulate camera feed with a placeholder image
      // In a real implementation, this would use WebRTC or similar technology
      setCameraFeed('/static/images/truck_camera_feed.jpg');

      setLoading(false);
    } catch (err) {
      console.error('Error fetching vision data:', err);
      setError('Failed to load vision data. Please try refreshing the page.');
      setLoading(false);
    }
  };

  // Handle snapshot button
  const takeSnapshot = () => {
    // In a real implementation, this would capture the current frame
    // and save it or process it with the vision system
    alert('Snapshot taken and saved.');
  };

  // Handle record button
  const toggleRecording = () => {
    // In a real implementation, this would start/stop recording
    alert('Recording toggled.');
  };

  // Toggle system active state
  const toggleSystem = () => {
    setSystemActive(!systemActive);
  };

  if (loading && !cameraFeed) {
    return (
      <div className="d-flex justify-content-center mt-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger">{error}</div>
    );
  }

  return (
    <div className="container-fluid px-4 py-3">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div className="d-flex align-items-center">
          <i className="fas fa-truck text-primary me-2" style={{ fontSize: '1.5rem' }}></i>
          <h1 className="mb-0">Autonomous Truck Vision System</h1>
        </div>
        <div className="d-flex align-items-center">
          <span className={`badge ${systemActive ? 'bg-success' : 'bg-danger'} me-2`}>
            System {systemActive ? 'Active' : 'Inactive'}
          </span>
          <button className="btn btn-light" onClick={toggleSystem}>
            <i className="fas fa-cog"></i>
          </button>
        </div>
      </div>

      <div className="row">
        {/* Camera Feed Column */}
        <div className="col-md-8">
          <div className="card mb-4">
            <div className="card-header d-flex justify-content-between align-items-center">
              <h5 className="mb-0">Live Camera Feed</h5>
              <div>
                <button className="btn btn-sm btn-outline-primary me-2" onClick={takeSnapshot}>
                  <i className="fas fa-camera me-1"></i> Snapshot
                </button>
                <button className="btn btn-sm btn-outline-danger" onClick={toggleRecording}>
                  <i className="fas fa-record-vinyl me-1"></i> Record
                </button>
              </div>
            </div>
            <div className="card-body p-0 position-relative">
              {/* Camera feed display */}
              {cameraFeed ? (
                <img 
                  src={cameraFeed} 
                  alt="Truck Camera Feed" 
                  className="img-fluid w-100"
                  style={{ maxHeight: '400px', objectFit: 'cover' }}
                />
              ) : (
                <div className="text-center py-5">
                  <i className="fas fa-video-slash fa-3x text-muted mb-3"></i>
                  <p>Camera feed unavailable</p>
                </div>
              )}

              {/* Overlay detection boxes - these would be dynamically positioned in a real app */}
              <div className="position-absolute" style={{ top: '20%', left: '15%', width: '20%', height: '60%', border: '2px solid #0d6efd', borderRadius: '4px' }}>
                <div className="bg-primary text-white px-2 py-1" style={{ position: 'absolute', top: '-25px', left: '0' }}>
                  Truck
                </div>
              </div>

              <div className="position-absolute" style={{ top: '30%', right: '20%', width: '25%', height: '40%', border: '2px solid #ffc107', borderRadius: '4px' }}>
                <div className="bg-warning text-dark px-2 py-1" style={{ position: 'absolute', top: '-25px', left: '0' }}>
                  Car
                </div>
              </div>

              <div className="position-absolute" style={{ bottom: '20%', left: '45%', width: '5%', height: '10%', border: '2px solid #dc3545', borderRadius: '4px' }}>
                <div className="bg-danger text-white px-2 py-1" style={{ position: 'absolute', top: '-25px', left: '0' }}>
                  !
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Stats Column */}
        <div className="col-md-4">
          <div className="card mb-4">
            <div className="card-header">
              <h5 className="mb-0">Detection Statistics</h5>
            </div>
            <div className="card-body">
              <div className="mb-3 d-flex justify-content-between align-items-center">
                <div>
                  <i className="fas fa-car text-primary me-2"></i> Vehicles Detected
                </div>
                <span className="badge bg-primary rounded-pill">{detectionStats.vehicles}</span>
              </div>

              <div className="mb-3 d-flex justify-content-between align-items-center">
                <div>
                  <i className="fas fa-walking text-info me-2"></i> Pedestrians
                </div>
                <span className="badge bg-info rounded-pill">{detectionStats.pedestrians}</span>
              </div>

              <div className="mb-3 d-flex justify-content-between align-items-center">
                <div>
                  <i className="fas fa-traffic-light text-success me-2"></i> Traffic Signals
                </div>
                <span className="badge bg-success rounded-pill">{detectionStats.traffic_signals}</span>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">Incident Detection</h5>
            </div>
            <div className="card-body p-0">
              {incidents.length === 0 ? (
                <div className="text-center py-4">
                  <p className="text-muted mb-0">No incidents detected</p>
                </div>
              ) : (
                <div className="list-group list-group-flush">
                  {incidents.map(incident => (
                    <div key={incident.id} className={`list-group-item list-group-item-${incident.severity === 'critical' ? 'danger' : 'warning'}`}>
                      <div className="d-flex w-100 justify-content-between">
                        <h6 className="mb-1">
                          {incident.severity === 'critical' && <i className="fas fa-exclamation-triangle me-2"></i>}
                          {incident.title}
                        </h6>
                        <small>{Math.floor((Date.now() / 1000 - incident.timestamp))} seconds ago</small>
                      </div>
                      <p className="mb-1">{incident.description}</p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Vision;
