import React, { useState, useEffect, useRef } from 'react';
import { AlertTriangle, AlertCircle } from 'lucide-react';
import api from '../services/api';
import Nav from '../Nav/Nav';

const Vision = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [detectionStats, setDetectionStats] = useState({
    vehicles: 0,
    pedestrians: 0,
    traffic_signals: 0,
  });
  const [detections, setDetections] = useState([]);
  const scrollRef = useRef(null);

  useEffect(() => {
    fetchVisionData();
    const interval = setInterval(fetchVisionData, 10000);
    return () => clearInterval(interval);
  }, []);

  const fetchVisionData = async () => {
    try {
      setLoading(true);
      const statsResponse = await api.get('/vision/stats');
      const detectionsResponse = await api.get('/vision/detections');

      setDetectionStats({
        vehicles: statsResponse.data.detection_counts.vehicle || 0,
        pedestrians: statsResponse.data.detection_counts.pedestrian || 0,
        traffic_signals:
          (statsResponse.data.detection_counts.traffic_light || 0) +
          (statsResponse.data.detection_counts.traffic_sign || 0),
      });

      setDetections(detectionsResponse.data || []);
      setError(null);
    } catch (err) {
      console.error('Error fetching vision data:', err);
      setError('Failed to load vision data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const scrollLeft = () => {
    scrollRef.current?.scrollBy({ left: -300, behavior: 'smooth' });
  };

  const scrollRight = () => {
    scrollRef.current?.scrollBy({ left: 300, behavior: 'smooth' });
  };

  const emergencyClasses = ['ambulance', 'police_car', 'fire_truck', 'maintenance_vehicle', 'accident', 'fire', 'smoke'];
  const emergencyDetections = detections.filter(d => emergencyClasses.includes(d.detection_type));

  return (
    <div className="flex flex-col md:flex-row h-screen w-screen">
      <Nav />
      <div className="flex h-screen w-full md:w-4/5 bg-gradient-to-br from-blue-50 to-gray-100">
        <main className="flex-1 p-6 space-y-6 overflow-y-auto">
          <div className="flex justify-between items-center p-4 bg-white shadow-md rounded">
            <h1 className="text-xl font-semibold">Vision Dashboard</h1>
            <div className="text-gray-600">Welcome, {localStorage.getItem('name')}.</div>
          </div>

          {error && <div className="text-red-600 font-semibold">{error}</div>}
          {loading ? (
            <div className="text-gray-600">Loading vision data...</div>
          ) : (
            <>
              {/* Stats Summary */}
              <div className="bg-white p-4 shadow rounded-xl">
                <h2 className="text-xl font-semibold mb-3">Detection Summary</h2>
                <div className="grid grid-cols-3 gap-4 text-center">
                  <div className="p-4 bg-blue-100 rounded">
                    <p className="text-sm text-gray-600">🚗 Vehicles</p>
                    <p className="text-lg font-bold">{detectionStats.vehicles}</p>
                  </div>
                  <div className="p-4 bg-green-100 rounded">
                    <p className="text-sm text-gray-600">🚶 Pedestrians</p>
                    <p className="text-lg font-bold">{detectionStats.pedestrians}</p>
                  </div>
                  <div className="p-4 bg-yellow-100 rounded">
                    <p className="text-sm text-gray-600">🚦 Signals</p>
                    <p className="text-lg font-bold">{detectionStats.traffic_signals}</p>
                  </div>
                </div>
              </div>

              {/* Scrollable Feed */}
              <div className="bg-white rounded-xl shadow-md p-4">
                <h2 className="text-xl font-semibold mb-2">Recent Detections</h2>
                <div className="flex justify-between items-center mb-2">
                  <button onClick={scrollLeft} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">◀</button>
                  <button onClick={scrollRight} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">▶</button>
                </div>
                <div ref={scrollRef} className="flex overflow-x-auto gap-4 pb-2">
                  {detections.map((det, index) => (
                    <div key={index} className="min-w-[280px] bg-gray-100 rounded-xl shadow p-3 flex-shrink-0">
                      <img
                        src={det.output_image_url || `/output/${det.image_name}`}
                        alt={det.image_name}
                        className="w-full h-40 object-cover rounded mb-2"
                      />
                      <p className="text-sm text-gray-600 mb-1">Image: {det.image_name}</p>
                      <p className="text-sm">🚗 Type: <strong>{det.detection_type}</strong></p>
                      <p className="text-xs text-gray-500 mt-1">Timestamp: {det.timestamp}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Incident Alerts */}
              <div className="bg-white rounded-xl shadow-md p-4">
                <h2 className="text-xl font-semibold mb-4">Incident Detection</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {emergencyDetections.map((incident, i) => (
                    <div key={i} className="bg-red-100 border-l-4 border-red-500 p-3 rounded-md">
                      <div className="flex items-center gap-2 font-medium text-red-700">
                        <AlertCircle size={18} /> Critical Incident
                      </div>
                      <p className="text-sm text-gray-800">
                        Detected {incident.detection_type} in {incident.image_name}
                      </p>
                      <p className="text-xs text-gray-500">{incident.timestamp}</p>
                    </div>
                  ))}
                </div>
              </div>
            </>
          )}
        </main>
      </div>
    </div>
  );
};

export default Vision;
